mapper.select:
select cs.* from cfg_type_source cts RIGHT join cfg_source cs on cs.id = cts.sourceid RIGHT JOIN cfg_type ct on ct.id = cts.typeid where cts.typeid = #{queryParam.typeid}
and (cs.verstion like concat("%",#{queryParam.verstion},"%"))
and (cs.platform like concat("%",#{queryParam.platform},"%"))
