mapper.select:
SELECT *, pc.parent_id cateParentId, l.id ladder_id,l.product_id ladder_product_id,l.discount ladder_discount,l.count ladder_count,l.price ladder_price, pf.id full_id,pf.product_id full_product_id,pf.full_price full_full_price,pf.reduce_price full_reduce_price, m.id member_id,m.product_id member_product_id,m.member_level_id member_member_level_id,m.member_price member_member_price,m.member_level_name member_member_level_name
FROM pms_product p LEFT JOIN pms_product_category pc on pc.id = p.product_category_id LEFT JOIN pms_product_ladder l ON p.id = l.product_id LEFT JOIN pms_product_full_reduction pf ON pf.product_id=p.id LEFT JOIN pms_member_price m ON m.product_id = p.id
WHERE p.id=#{id};
